"use strict";

var A = require("assert");

exports.ok = function (val) {
	return A.ok(val);
};

exports.eq = function (a, b) {
	return A.strictEqual(a, b);
};

exports.isNum = function (val) {
	return exports.eq(typeof val, "number");
};

exports.isStr = function (val) {
	return exports.eq(typeof val, "string");
};

exports.isArr = function (val) {
	return exports.ok(Array.isArray(val));
};

exports.has = function (val, arr) {
	return exports.ok(arr.indexOf(val) + 1);
};
